jQuery(document).ready(function(){
		
	$("#view-notifications").click(function(){
		$("#view-mainmenu").hide();			
		$("#notification-list").toggle();
	});
	$("#view-mainmenu").click(function(){
		$("#view-notifications").hide();			
		$("#mainmenu-list").toggle();
	});
});

	//display the number of chars enteres
	function count_chars(inputfield, div_char_counter, max_chars){
		$("#"+div_char_counter).html(inputfield.val().length+"/"+max_chars+" caracteres");	
	}
	
